authFunction:
function authFunction(uname, password, $http, $rootScope, success, error) {
  /* callAPI, refreshToken and callOpenAPI are moved to services in framework */

  var url = $rootScope.GatewayUrl + "/unifydidentity/open/oauth2/token";

  

  $.ajax({
    type: 'POST',
    url: url,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'X-TENANT-ID': $rootScope.user.tenant
    },
    data: {
      username: uname,
      password: password
    },
    dataType: 'json',
    encode: true
  }).done(function(res) {
    success(res);
  }).fail(function(err) {
    $.unblockUI();
    if (window.device) {
      navigator.notification.alert("Unable to login, please check your credentials", null, '', 'Ok');
    } else {
      alert("Unable to login, please check your credentials");
    }

  });

}

pages.pageprocessor:
function pageprocessorTransit5(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var iabRef;
  if (window.device) {
    $.blockUI();
    iabRef = window.open(pagedef.url, "_blank", "location=no,hidden=yes,enableViewportScale=yes,clearcache=yes");
  } else {
    iabRef = window.open(pagedef.url, "_blank");

  }

  if (!window.device) {
    window.history.back();
  }

  var hideBlockUi = function() {
    $.unblockUI();
    iabRef.show();
  };
  var iabClose = function(data) {
    $.unblockUI();
    iabRef.removeEventListener("exit", iabClose);
    window.screen.orientation.lock('portrait')
    window.history.back();
  };
  iabRef.addEventListener("exit", iabClose);
  var loadStop = function(data) {
    hideBlockUi();
    iabRef.removeEventListener("loadstop", loadStop);
  };
  iabRef.addEventListener("loadstop", loadStop);
}

