         return callBack({"error": "Error executing API"});
         });
         */
    var headers = {
      "Content-Type": "application/json"
    }
    url = "/proxy";
    var postbody = {
      url: serviceUrl,
      method: method,
      headers: headers,
      json: body
    }
    $http({
      method: "POST",
      url: url,
      data: postbody
    }).then(function successCallBack(res) {
      return callBack(res.data);
    }, function errorCallback(err) {
      return callBack({
