      }


    } catch (e) {
      console.log(e);
    }

  };

  $scope.trustSrc = function(url) {
    return $sce.trustAsResourceUrl('https://www.youtube.com/embed/' + url);
  };


  /*fix message count issue */
  function lineBreakCount(str) {
    /* counts \n */
    try {
      var a = str.match(/[^\n]*\n[^\n]*/gi).length;
      var b = str.match(/[^\r]*\r[^\r]*/gi).length;
      return (a + b);
    } catch (e) {
      return 0;
    }
  }


  /*function for preview messageDetail*/
  $scope.messageDetail = function(message, channel) {
    setTimeout(function() {
      $('.modal-backdrop').hide();
    }, 100);
    var appendtrans = '<div class="modal-backdrop fade in"></div>';
    $('.message_main').append(appendtrans);

    $scope.messageData = {};
    $scope.emailmessage = "";
    $scope.connectmessage = "";
    $scope.smsmessage = "";
    $scope.notificationmessage = "";
    $scope.emailtitle = "";
    $scope.postImage = "";
    $scope.youtubeurl = "";

    $scope.channelCount = 0;
    $scope.selChannel = 0;
    console.log("sel channel = " + channel);
    $scope.selChannel = channel;
    $('.previewItem').removeClass('active');
    if ($scope.selChannel == "connect") {
      $('#connectCarousel').addClass('active messagePreview');
    } else if ($scope.selChannel == "sms") {
      $('#smsCarousel').addClass('active messagePreview');
    } else if ($scope.selChannel == "notification") {
      $('#notificationCarousel').addClass('active messagePreview');
    } else if ($scope.selChannel == "email") {
      $('#emailCarousel').addClass('active messagePreview');
    }

    if (message != undefined && message) {
      $scope.messageData = message;
      $scope.channelCount = message.channels.length;
      console.log("test", $scope.channelCount);
      console.log("message", message);
      if (message.channelMsg["email"] != null || message.channelMsg["email"] != undefined) {
        $scope.emailmessage = message.channelMsg["email"].replace(/<br\s*[\/]?>/gi, "\r\n");
        var lineBreakEmail = lineBreakCount($scope.emailmessage);
        $scope.emailMessageCount = $scope.emailmessage.length - lineBreakEmail;
      }

      if (message.channelMsg["whatsUp"] != null || message.channelMsg["whatsUp"] != undefined) {
        $scope.connectmessage = message.channelMsg["whatsUp"].replace(/<br\s*[\/]?>/gi, "\r\n");
        var lineBreakConnect = lineBreakCount($scope.connectmessage);
        $scope.connectMessageCount = $scope.connectmessage.length - lineBreakConnect;
      }
      if (message.channelMsg["sms"] != null || message.channelMsg["sms"] != undefined) {
        $scope.smsmessage = message.channelMsg["sms"].replace(/<br\s*[\/]?>/gi, "\r\n");
        var lineBreakSms = lineBreakCount($scope.smsmessage);
        $scope.smsMessageCount = $scope.smsmessage.length - lineBreakSms;
      }
      if (message.channelMsg["notification"] != null || message.channelMsg["notification"] != undefined) {
        $scope.notificationmessage = message.channelMsg["notification"].replace(/<br\s*[\/]?>/gi, "\r\n");
        var lineBreakNotification = lineBreakCount($scope.notificationmessage);
        $scope.notificationMessageCount = $scope.notificationmessage.length - lineBreakNotification;
      }

      if (message.title)
        $scope.emailtitle = message.title;
      $scope.notificationtitle = message.title;


      if (message.graphicType == "image")
        $scope.postImage = message.graphicUrl;
      else if (message.graphicType == "video")
        $scope.youtubeurl = message.graphicUrl;

      if (message.graphicUrl) {
        $scope.notificationUrl = message.graphicType == 'image' ? message.graphicUrl : 'https://img.youtube.com/vi/' + message.graphicUrl + '/hqdefault.jpg';
      }

      console.log("tags", message.tags)
      if (message.tags != undefined) {
        if (message.tags.length > 0) {
          var url = '/unifyedcategory/category?page=0&size=1000&sort=category,asc';
          $rootScope.callAPI(url, 'GET', {}, function(res) {
            $scope.connectCategory = res['data']['_embedded']['category'];
            console.log("connectCategory", $scope.connectCategory)
            var category = []
            for (var i = 0; i < $scope.connectCategory.length; i++) {
