    }
    channelStr = (channelStr) ? "&channels=" + channelStr : "";
    status = (status) ? "&status=" + status : "";
    if (channelStr || status) {
      $scope.searchCase = 1;
    }

    var url = "/unifydmessaging/feed/search?user=" + $rootScope.user.email + "&sort=createdDate,DESC&size=5&page=" + page + channelStr + status;
    $.blockUI();
