              smsg = smsg ? smsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
        
              messageObject['channelMsg']['sms'] = smsg;*/
  
        /*nmsg = nmsg ? nmsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
              nmsg = nmsg ? nmsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
              nmsg = nmsg ? nmsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
        
              messageObject['channelMsg']['notification'] = nmsg;*/
  
  
        emsg = emsg ? emsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
        emsg = emsg ? emsg.replace(/(http:\/\/[^\s]+)/gi, '<a  href="$1" target="blank">$1</a>') : '';
        emsg = emsg ? emsg.replace(/(https:\/\/[^\s]+)/gi, '<a  href="$1" target="blank">$1</a>') : '';
  
        messageObject['channelMsg']['email'] = emsg;
  
  
        dmsg = dmsg ? dmsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
        dmsg = dmsg ? dmsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1" target="blank">$1</a>') : '';
        dmsg = dmsg ? dmsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1" target="blank">$1</a>') : '';
  
        messageObject['defaultMessage'] = dmsg;
  
  
        if (!$rootScope.updateMessage) {
  
          console.log("new post test", messageObject);
  
          var url = '/unifydmessaging/postMessage';
          $rootScope.callAPI(url, 'POST', messageObject, function(res) {
