          function autoheight(a) {
            if (!$(a).prop('scrollTop')) {
              do {
                var b = $(a).prop('scrollHeight');
                var h = $(a).height();
                $(a).height(h - 5);
              }
              while (b && (b != $(a).prop('scrollHeight')));
            };
