    window.open($scope.selEvent.link, "_blank");
  };

  var data = pagedef.pageTemplate;
  data = $(data).append($(pagedef.datatemplate));
  console.log(JSON.stringify(data));
  var data1 = $compile($(data))($scope);
  $("#appContent").append(data1);

  $scope.trustHtml = function(data) {
    if (!data) {
      return data;
    }

    data = data.replace(/(?:\\r\\n|\\r|\\n)/g, '<br/>');
    data = data.replace(/(?:\\)/g, '');
    return $sce.trustAsHtml(data);
  }

  $scope.openFeed = function() {
    $location.path("/app/Events/Events");
    return;
    /*    $rootScope.Events = $scope.index;
        $rootScope.index = index;
        $scope.Eventsdata = $rootScope.Events.Eventsdata;
        console.log($scope.Eventsdata);
        $location.path("/app/Events/Events");*/
  };

  $scope.next = function() {
    var len = $rootScope.Eventsdata.length;
    console.log(len);
    try {
      console.log($scope.selIndex);
      if ($scope.selIndex < (len - 1)) {
        $scope.selIndex++;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };

  $scope.prev = function() {
    try {
      console.log($scope.selIndex);

      if ($scope.selIndex > 0) {
        $scope.selIndex--;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
}

function pageprocessorConnect(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $rootScope.callConnectAPI = function(url, method, data, callback) {
    try {

      if (!$rootScope.isblocking) $.blockUI();
      var apiEndPoint = $rootScope.GatewayUrl + url;
      var headers = {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'Authorization': 'Bearer ' + $rootScope.user.accessToken,
        'X-TENANT-ID': $rootScope.user.tenant,
        'X-TENANT-DOMAIN': $rootScope.user.tenantdomain,
        'principal-user': ($rootScope.webApp) ? $rootScope.user.email : $.jStorage.get("unifyedusername")
      }


      if ($rootScope.site && $rootScope.site._id) {
        headers['site-id'] = $rootScope.site._id;
      }
      var req = {
        "method": method,
        "url": apiEndPoint,
        "headers": headers,
        data: data,
        json: true
      };


      $http(req).then(function successCallback(res) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }

        return callback(res);
      }, function errorCallback(err) {
        console.log(JSON.stringify(err));
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        //return callback();
        /* Commented the code below since token refersh is not working */
        if (err.status == 404) {
          return callback(null);
        } else if (err.status == 0 || err.status == 401) {
          $rootScope.refreshToken(url, method, data, callback);
          return callback();
        }
      });
    } catch (e) {
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      console.log(e)
      return callback();
    }
  }
