        params = "criteria=" + criteria + "&useremail=" + $rootScope.user.email + "&group=" + groups + "&size=10&sort=updatedDate,DESC&page=" + page;
      } else {
        console.log('normal connect categories');
        groups = $rootScope.user['role'].toString();
        criteria = (groups) ? $rootScope.user.email + "," + groups : $rootScope.user.email;
        params = "criteria=" + criteria + "&useremail=" + $rootScope.user.email + "&size=10&sort=updatedDate,DESC&page=" + page;
      }      
      if (window.device) {
        var feedUrl = "/unifydwhatsup/open/connectfeed?" + params;
      } else {
        var feedUrl = "/unifydwhatsup/open/connectfeed?platform=webapp&" + params;
      }
      $rootScope.callConnectAPI(feedUrl, 'GET', '', function(data) {
        var allcategory = data.data.allcategory;
        allcategory.unshift({
          "id": "all",
          "category": "All",
          "enable": true,
          "selected": false
        });
        $scope.areaOfInterest = allcategory;
        $rootScope.areaOfInterest = allcategory;
        if (!$rootScope.isUserTenantAdmin) {
          var myadmincategory = data.data.myadmincategory;
          if (myadmincategory.length > 0) {
            $rootScope.isUserWhatsUpAdmin = true;
            var userWhatsUpAdminCategory = [];
            $scope.userWhatsUpAdminCategoryIds = [];
            for (var i = 0; i < myadmincategory.length; i++) {
              userWhatsUpAdminCategory.push(myadmincategory[i].category);
              $scope.userWhatsUpAdminCategoryIds.push(myadmincategory[i].id);
            }
            for (var i = 0; i < $scope.areaOfInterest.length; i++) {
              $scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? userWhatsUpAdminCategory.indexOf($scope.areaOfInterest[i]['category']) != -1 : false;
              $scope.areaOfInterest[i].selected = false;
            }
          }
        } else {
          for (var i = 0; i < $scope.areaOfInterest.length; i++) {
            $scope.areaOfInterest[i].selected = false;
          }
        }
        $scope.userAOI = [];
        $rootScope.user.areaOfInterest = data.data.areaOfInterest;
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
          if ($rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) {
            $scope.userAOI.push($scope.areaOfInterest[i].category);
          }
        }
        if ($scope.userAOI.length == 0) {
          if (window.device) {
            navigator.notification.alert("Please choose your preferences.", null, ' ', 'Ok');
          } else {
            alert("Please choose your preferences.");
            $location.path("/app/Connect/ConnectPage6");
          }
        }

        var feed = data.data.feed;
        if (feed.last) {
          $scope.hideLoadMore = false;
        } else {
          $scope.hideLoadMore = true;
        }
        for (var i = 0; i < feed.content.length; i++) {
          feed.content[i].limitComment = -3;
          feed.content[i].showViewAllComment = true;
        }
        $scope.posts = feed.content;
        $rootScope.post = feed.content;
        if ($scope.posts.length == 0) {
          $scope.emptyPost = 1;
        }
        $scope.setUp();
      });
    } catch (ex) {
      alert(ex);
