      if (l == 0) {
        $(".replybtn").removeClass('yellowText').addClass('replybtnText');
      } else {
        $(".replybtn").removeClass('replybtnText').addClass('yellowText');
      }
    }
    $scope.postComment = function() {
      $scope.comment = $('#commentBox').val();
      if ($scope.comment) {
        var feedUrl = "/unifydwhatsup/comment";
        var data = {
          "postId": $scope.p.id,
          "username": $rootScope.user.firstName + " " + $rootScope.user.lastName,
          "userimage": $rootScope.user.profileImage,
          "userRole": $rootScope.user.role,
          "comment": $scope.comment
        }
        $rootScope.callConnectAPI(feedUrl, 'POST', data, function(data) {
          if (!data.data.error) {
            console.log('data response');
            $scope.p.comment += 1;
            $scope.p.comments.push(data.data);
            if (!$scope.p.showViewAllComment) {
              $scope.p.limitComment = $scope.p.comment;
            }
            $scope.comment = "";
            $('#commentBox').val('');
            $(".replybtn").removeClass('yellowText').removeClass('themeText').addClass('replybtnText');
            $(".footerGroupIcons").css('display', 'block !important');
            $("#bottomPoweredBy").removeClass('hideFooter');
            $('.scrollable-content').animate({
              scrollTop: $('#appContent').height() + 1000
            }, 'slow');
            $scope.$digest();

          }
        });
      } else {
        if (window.device) {
          navigator.notification.alert('Please add comment.', null, 'Comment', 'Ok')
        } else {
          alert('Please add comment.');
        }
      }
    }

    $scope.loadLikes = function(id) {
      $("#showPostLikes" + id).show();
    }

    $scope.hidePostLikes = function(id) {
      $("#showPostLikes" + id).hide();
    }
    $scope.showDelText = function(id) {
      $("#delTextComment" + id).removeClass('ng-hide').addClass('ng-show');
    }
    $scope.hideDelText = function(id) {
      $("#delTextComment" + id).removeClass('ng-show').addClass('ng-hide');
    }
    $scope.showComment = function(id) {
      $("#comment" + id).removeClass('ng-hide').addClass('ng-show');
    }
    $scope.hideComment = function(id) {
      $("#comment" + id).removeClass('ng-show').addClass('ng-hide');
    }
    $scope.goBack = function() {
      $location.path("/app/Connect/Connect");
    }
    $scope.addComment = function() {
      $("#footerContentContainer").removeClass('footerGroupIcons').addClass('hideFooter');
      $("#bottomPoweredBy").addClass('hideFooter');
      $("#fixedFooterContent").css('bottom', footerHeight + 'px');
      $('body').css('position', 'fixed')
        //$('.scrollable-content').css('padding-bottom',btmPadding-btm+'px');

      $("#commentBox").focus();
      setTimeout(function() {
        $('.scrollable-content').animate({
          scrollTop: $('#appContent').height() + 1000
        }, 'slow');
      }, 50)
      return false;
    }

    $scope.showPostLikes = function() {

      $rootScope.postLikes = $scope.p.like;
      $location.path("/app/Connect/ConnectPage4");
    }


    $scope.likeComment = function(id) {
      if ($scope.comments[id].commentLiked) {
        $scope.comments[id].commentLiked = false;
      } else {
        $scope.comments[id].commentLiked = true;
      }
    }

    $scope.delComment = function(id) {
      var DeleteCommentIndex = $scope.p.comments.findIndex(function(x) {
        return x.id == id
      });

      var userdelPost = function(btn) {
        if (btn == 1) {
          var postUrl = "/unifydwhatsup/comment/" + id;
          $rootScope.callConnectAPI(postUrl, 'DELETE', '', function(data) {
            /* Add condition here to check successful deletion. By passing it as notification service is down */
            $scope.p.comments.splice(DeleteCommentIndex, 1);
            $scope.p.comment -= 1;
          });
        }
      }


      if ($rootScope.webApp) {
        userdelPost(1);
      } else {
        var options = {
          'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
          'title': '',
          'buttonLabels': ['Delete Comment', 'Cancel']
        };
        window.plugins.actionsheet.show(options, userdelPost);
      }
    }
    $scope.openPostVideo = function(e, id) {
      e.stopPropagation();
      window.open('https://www.youtube.com/watch?v=' + id, '_blank');
    }

  } catch (e) {}

  $scope.trustSrc = function(videoID) {
    return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0");
  };

  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(data);
  }
}

