  $scope.loadUserPref = function() {
    var url = '/unifyedcategory/category';

    $rootScope.callConnectAPI(url, 'GET', {}, function(res) {

      var data = res.data;

      if (data['exception']) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to get preferences.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to get preferences.');
        }
        return;

      }
      //console.log('category ' + JSON.stringify(data));
      data['_embedded']['category'].unshift({
        "id": "all",
        "category": "All",
        "enable": true,
        "selected": false
      });

      $scope.areaOfInterest = data['_embedded']['category'];
      $rootScope.areaOfInterest = data['_embedded']['category'];

      if (!$rootScope.isUserTenantAdmin) {
        //url = '/unifyedcategory/category/search/findByWhatsupAdmins?email=' + $rootScope.user.email;
        var groups = $rootScope.user['role'].toString();
        var criteria = (groups) ? $rootScope.user.email + "," + groups : $rootScope.user.email;
        url = '/unifyedcategory/category/search/usercategories?criteria=' + criteria;
        $rootScope.callConnectAPI(url, 'GET', {}, function(res) {
          if (res.data['_embedded']['category'].length > 0) {
            $rootScope.isUserWhatsUpAdmin = true;
            var userWhatsUpAdminCategory = [];
            $scope.userWhatsUpAdminCategoryIds = [];
            for (var i = 0; i < res.data['_embedded']['category'].length; i++) {
              userWhatsUpAdminCategory.push(res.data['_embedded']['category'][i].category);
              $scope.userWhatsUpAdminCategoryIds.push(res.data['_embedded']['category'][i].id);
            }
            //console.log('userwhatsUpAdmin: ' + $scope.userWhatsUpAdminCategoryIds);
            for (var i = 0; i < $scope.areaOfInterest.length; i++) {
              $scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? userWhatsUpAdminCategory.indexOf($scope.areaOfInterest[i]['category']) != -1 : false;
              $scope.areaOfInterest[i].selected = false;
            }
          }
        });
      } else {
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
          //$scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? ($rootScope.user.areaOfInterest != undefined && $rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) : false;
          $scope.areaOfInterest[i].selected = false;
        }
      }
      //console.log($scope.areaOfInterest);

      $scope.userAOI = [];
      for (var i = 0; i < $scope.areaOfInterest.length; i++) {
        //console.log(JSON.stringify($rootScope.user.areaOfInterest) + " " + $scope.areaOfInterest[i]['id']);
        if ($rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) {
          $scope.userAOI.push($scope.areaOfInterest[i].category);
        }
      }

      //console.log($scope.userAOI);

      if ($scope.userAOI.length == 0) {
        if (window.device) {
          navigator.notification.alert("Please choose your preferences.", null, ' ', 'Ok');
        } else {
          alert("Please choose your preferences.");
          $location.path("/app/Connect/ConnectPage6");
        }
        return;
      }

      //    $scope.loadPosts();

    });
  }

