        $location.path("/app/Connect/Connect");

        /*$scope.showCreatePost = false;
         $scope.postImage = '';
         $scope.imageSource = '';
         $scope.videoId = '';
         $scope.AddTagsBtn = true;
         $scope.showAddTag = true;
         $scope.showTagsList = false;
         $scope.noOfselTags = 0;
         $scope.showUploadPhoto = false;
         $scope.image = '';
         document.getElementById('postTitle').value = '';
         $scope.postTitle = '';
