            $http.post(url, data).then(function(resp) {
              if (resp.data.url) {
                $scope.imagesrc = resp.data.url;
                $scope.createNewPost();
                $(".postDesc a").on('click', function(e) {
                  console.log(1)
                  var url = $(this).attr('href');
                  var target = $(this).attr('target');
                  if (target == '_blank') {
                    e.preventDefault();
                    e.stopPropagation();
                    window.open(url, '_blank');
                  }
                });
              } else {
                $.unblockUI();
              }

            });
          } else {
            $scope.createNewPost();
          }
        }
      } catch (e) {
        alert(e)
      }
    }
