        $.each($rootScope.DirectoryList[pagedef['item_id']], function(index, val) {
            var found = false;
            if ($scope.departmentDropDownHtml && $scope.selDepartment != "All") {
              ///  console.log("-------------1--",$scope.selDepartment);
              //  console.log("------2----",val.department);
                if ($scope.selDepartment == val.department) {
                    for (var i = 0; i < filterParams.length; i++) {
                        var key = "" + filterParams[i];
                        if (val[key] != undefined && val[key].match(patt)) {
                            results.push(val);
                            break;
                        }
                    }
                }
            } else {

                for (var i = 0; i < filterParams.length; i++) {
                    var key = "" + filterParams[i];
                    if (val[key] != undefined && val[key].match(patt)) {
                        results.push(val);
                        break;
                    }


                }
            }
        });





        results.sort(function(a, b) {
            if ($rootScope.sort == "on") {
                if (a[$rootScope.firstName] < b[$rootScope.firstName])
                    return -1;
                if (a[$rootScope.firstName] > b[$rootScope.firstName])
                    return 1;
                return 0;
            } else {
                if (a[$rootScope.lastName] < b[$rootScope.lastName])
                    return -1;
                if (a[$rootScope.lastName] > b[$rootScope.lastName])
                    return 1;
                return 0;
            }
        });

        $rootScope.DirectoryResult = results;
        //console.log("test",results);
