    } else if ($rootScope.DirectoryResult[index - 1][$rootScope.firstName].substring(0, 1) == $rootScope.DirectoryResult[index][$rootScope.firstName].substring(0, 1)) {
      return;
    } else {
      return $rootScope.DirectoryResult[index][$rootScope.firstName].substring(0, 1).toUpperCase();
    }
  }else{
