function pageprocessorEDUDirectoryPage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $scope.DirectoryDetails = $rootScope.directoryUserInfo;
  var template = pagedef.datatemplate;
  //console.log(pagedef);
  var data1 = $compile($(template))($scope);
  $("#appContent")
    .append(data1);

  $scope.addContacts = function() {
    var dirContact = navigator.contacts.create();
    var details = $scope.DirectoryDetails;

    if (window.device.platform == "Android") {
      dirContact.displayName = details[$rootScope.firstName] + " " + details[$rootScope.lastName];
      var phoneNo = details[$rootScope.phone];
      if (phoneNo) {
        var phoneNumbers = [1];
        phoneNumbers[0] = new ContactField('work', phoneNo, true); // preferred number  
        dirContact.phoneNumbers = phoneNumbers;
      }

      var personEmail = details[$rootScope.email];

      if (personEmail) {
        var emails = [1];
        emails[0] = new ContactField('emails', personEmail, false);
        dirContact.emails = emails;
      }

      ///for ios displayName is not supported insted of displayName we can use name object
      var name = new ContactName();

      if (details[$rootScope.firstName] == "n/a") {
        name.givenName = "";
      } else {
        name.givenName = details[$rootScope.firstName];
      }

      if (details[$rootScope.lastName] == "n/a") {
        name.familyName = "";
      } else {
        name.familyName = details[$rootScope.lastName];;
      }

      dirContact.name = name;

      dirContact.save(preLoginSaveContacts, preLoginSaveError);
    }


    if (window.device.platform == "iOS") {

      var phoneNumbers = [2];
      // phoneNumbers[0] = new ContactField('mobile', $("#phone").text(), true); // preferred number
      phoneNumbers[0] = new ContactField('work', details[$rootScope.phone], true); // Work/Dept. number
      dirContact.phoneNumbers = phoneNumbers;

      var emails = [1];
      emails[0] = new ContactField('emails', details[$rootScope.email], false);
      dirContact.emails = emails;

      ///for ios displayName is not supported insted of displayName we can use name object
      var name = new ContactName();
      if (details[$rootScope.firstName] == "n/a") {
        name.givenName = "";
      } else {
        name.givenName = details[$rootScope.firstName];
      }

      if (details[$rootScope.lastName] == "n/a") {
        name.familyName = "";
      } else {
        name.familyName = details[$rootScope.lastName];;
      }

      dirContact.name = name;

      dirContact.save(preLoginSaveContacts, preLoginSaveError);
    }

  }

  function preLoginSaveContacts(contact) {
    navigator.notification.alert("Contact Saved Successfully");
  }

  function preLoginSaveError(contactError) {
    if (contactError.code == 20) {
      navigator.notification.alert("Permission denied");
    } else {
      navigator.notification.alert("Contact already saved");
    }
  }

  $scope.goBack = function() {
     $.blockUI();
    $location.path("/app/EDUDirectory/EDUDirectory");
  }

}

