        $scope.total_ytd_empr_amt = $scope.total_ytd_empr_amt + ((value.EMPLOYER_YTD) ? value.EMPLOYER_YTD : 0 );
      }
      var currDed = $scope.getCurrentPayDetails(value.PHRDEDN_BDCA_CODE);
