    $scope.transcripts = "data";
    var inst_gpa = 0;
    var tran_gpa = 0;
    var inst_qual = 0;
    var tran_qual = 0;
    var gpa_hours = 0;
    angular.forEach($scope.listdata.transcripttotal, function(value, key) {
      if (value.TRANSCRIPT_TYPE == "I") {
        $scope.totalinst = value;
        inst_qual = parseFloat(value.QUALITY_POINTS);
        inst_gpa = parseFloat(value.GPA_HOURS);
      } else if (value.TRANSCRIPT_TYPE == "T") {
        $scope.totaltrans = value;
        tran_qual = parseFloat(value.QUALITY_POINTS);
        tran_gpa = parseFloat(value.GPA_HOURS);
      } else if (value.TRANSCRIPT_TYPE == "Z") {
        $scope.cumulative = value;
        if (inst_gpa + tran_gpa > 0) {
          gpa_hours = (inst_qual + tran_qual) / (inst_gpa + tran_gpa);
        } else {
          gpa_hours = (inst_qual + tran_qual);
        }
        $scope.cumulative.GPA = gpa_hours;
      }
    });
    // }
  }


  $scope.selectTab = function(tab) {
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#tab3").removeClass("toolbar-column-active");
    $("#" + tab).addClass("toolbar-column-active");
    if (tab == "tab1") {
      $scope.selTab = 1;
      $scope.selTabAcdamic = 'tab1';
    } else if (tab == "tab2") {
      $scope.selTab = 2;
      $scope.selTabAcdamic = 'tab2';
    } else if (tab == "tab3") {
      $scope.selTab = 3;
      $scope.selTabAcdamic = 'tab3';
    }
  }

  function initApplet() {

    $rootScope.getAPI(config, bannerblockTranscriptsEndpoint, function(resp) {
      if (resp.blockTranscriptsGrades.length > 0) {
        //MM: commented for testing: if (resp.blockTranscriptsGrades[0].BLOCKTRANSCRIPTS == 'Y' || resp.blockTranscriptsGrades[0].BLOCKGRADES == 'Y' ) {
        if (resp.blockTranscriptsGrades[0].BLOCKTRANSCRIPTS == 'N') {
          showAlert("Your transcript is not available due to holds on your record.");
        } else {
          $rootScope.getAPI(config, bannertranscriptdropdownEndpoint, function(data) {
            $scope.dropdown = data;
            if ($scope.dropdown.transcripttypedropdown.length > 0) {
				if(autoSelectTerm == "on"){
                $scope.dropdownselect($scope.indexTerm);     
				}				
            } else {
              showAlert("No information is available for your account.");
            }
          });
        }
      } else {
        $rootScope.getAPI(config, bannertranscriptdropdownEndpoint, function(data) {
          $scope.dropdown = data;
          if ($scope.dropdown.transcripttypedropdown.length > 0) {
			  if(autoSelectTerm == "on"){
              $scope.dropdownselect($scope.indexTerm);
			  }
            
          }
        });
      }
    });


  }

  initApplet();





}

function pageprocessorYouTube20(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location,services,studioParam) {
  $scope.studioParam = studioParam;
  $.blockUI()
  $("#fixedFooterContent").html('');
  var url = "https://www.googleapis.com/youtube/v3/search?videoEmbeddable=true&order=date&part=snippet&channelId=" + pagedef.url + "&type=video&maxResults=50&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw";

  $http({
    'url': url,
    'method': 'GET'
  }).then(function successCallBack(data) {
    $.unblockUI();
    var data = data.data;
    if (data.items.length == 0) {
      if (window.device) {
        navigator.notification.alert(
          'No video found for given channel id.',
          null,
          '',
          'Ok'
        );
      } else
        alert('No video found for given channel id.');

      window.history.back();
      return;
    }
    for (var i = 0; i < data.items.length; i++) {
      data.items[i].playing = false;
    }

    $scope.videos = data.items;
    $scope.videos[0].playing = true;
    $rootScope.videos = data.items;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.loadVideo(0);

  }, function errorCallBack(data) {
    $.unblockUI();
    if (window.device) {
      navigator.notification.alert(
        'Incorrect channel id.',
        null,
        '',
        'Ok'
      );
    } else
      alert('Incorrect channel id');
    window.history.back();
  });

  $scope.navigate = function(index) {
    if ($rootScope.webApp != undefined) {
      $scope.loadVideo(index);
    } else {
      $rootScope.index = index;
      $location.path("/app/YouTube20/YouTube20VideosDetails");
    }
      var scroll_pos=($('#myHeader').position().top);
      $("#appContent , #pageContent").animate({ scrollTop: (scroll_pos) }, "slow");
  }


  $scope.loadVideo = function(index) {
    for (var i = 0; i < $scope.videos.length; i++) {
      $scope.videos[i].playing = false;
    }
    var video = $scope.videos[index];

    video.playing = true;
    var videoID = video.id.videoId;
    $scope.publishDate = videoID.publishedAt;
    var url = "https://www.googleapis.com/youtube/v3/videos?id=" + videoID + "&key=AIzaSyCsgCy_wu22gBBf-jewPk76QN9mUzevkGw&part=snippet,contentDetails,statistics,status";
    $http({
      'method': 'GET',
      'url': url
    }).then(function successCallBack(data) {
      var data = data.data;
      $scope.title = data.items[0].snippet.localized.title;
      $scope.publishDate = data.items[0].snippet.publishedAt;
      $scope.trustSrc = function() {
        return $sce.trustAsResourceUrl("https://www.youtube.com/embed/" + videoID + "?rel=0&autoplay=1");
      };
      $scope.description = data.items[0].snippet.localized.description;
    });
  }
}

