    $scope.loadPage = function() {
      // Get schedule data from Banner
      $scope.studentschedule = [];
      var scheduleEndpoint = (demoAPI) ? "/bannerweekataglance" : currentScheduleEndpoint + "/termCode=" + $scope.selectedtermCode;
      if (!demoAPI && !$scope.selectedtermCode) {
        //No valid term, exit 
        return;
      }
      $scope.currentSchedule = [];
      $scope.courseTitleDropDown = [];
      $scope.scheduleSummary = "";

      //var data = dummyData;
      $rootScope.getAPI(config, scheduleEndpoint, function(data) {
        console.log("Results");
        if (demoAPI) {
          data = dummyData;
        }
        console.log(data);
        $scope.totalData = data.weekataglance;
        console.log("schedule");
        console.log($scope.totalData);
        $scope.courseTitleDropDown = data.courseTitleDropDown;
        if (data.p_summary_out[0].summary && typeof data.p_summary_out[0].summary == "string") {
          $scope.scheduleSummary = JSON.parse(data.p_summary_out[0].summary);
        }
        console.log("summary");
        console.log($scope.scheduleSummary);
        if (data.p_schedule_out[0].currentschedule && typeof data.p_schedule_out[0].currentschedule == "string") {
          $scope.currentSchedule = JSON.parse(data.p_schedule_out[0].currentschedule);
        }
        console.log("currentschedule");
        console.log($scope.currentSchedule);
        var WeekAtGlanceTdyDate = new Date();
        var WeekAtGlanceStartDate = new Date();
        var WeekAtGlanceEndDate = new Date();
        WeekAtGlanceStartDate.setDate(WeekAtGlanceTdyDate.getDate() - WeekAtGlanceTdyDate.getDay());
        WeekAtGlanceEndDate.setDate(WeekAtGlanceTdyDate.getDate() + (6 - WeekAtGlanceTdyDate.getDay()));
        $scope.todayCount = 0;
        $scope.tomorrowCount = 0;
        angular.forEach(data.weekataglance, function(value, key) {
          var ClassStartDate = new Date(value.START_DATE);
          var ClassEndDate = new Date(value.END_DATE);
          value.active = false;
          if ((ClassStartDate <= WeekAtGlanceStartDate && ClassEndDate >= WeekAtGlanceEndDate) ||
            (ClassStartDate <= WeekAtGlanceEndDate && ClassStartDate >= WeekAtGlanceStartDate) ||
            (ClassEndDate <= WeekAtGlanceEndDate && ClassEndDate >= WeekAtGlanceStartDate)) {
            value.active = true;
          }
          value.dropAllowed = $scope.isCourseDroppable(value.CRN);
          value.sortorder = (value.active) ? 0 : 1;
          var currentDate = new Date();
          var dayIndex = currentDate.getDay();
          var weekDay = $scope.weekdays[dayIndex - 1];
          console.log("today: " + weekDay);
          value.repeats = "";
          value.repeats = (value.MON_DAY) ? weekShortNames[0] : "";
          value.repeats += (value.TUE_DAY) ? ((value.repeats) ? ", " + weekShortNames[1] : weekShortNames[1]) : "";
          value.repeats += (value.WED_DAY) ? ((value.repeats) ? ", " + weekShortNames[2] : weekShortNames[2]) : "";
          value.repeats += (value.THU_DAY) ? ((value.repeats) ? ", " + weekShortNames[3] : weekShortNames[3]) : "";
          value.repeats += (value.FRI_DAY) ? ((value.repeats) ? ", " + weekShortNames[4] : weekShortNames[4]) : "";
          value.repeats += (value.SAT_DAY) ? ((value.repeats) ? ", " + weekShortNames[5] : weekShortNames[5]) : "";
          value.repeats += (value.SUN_DAY) ? ((value.repeats) ? ", " + weekShortNames[6] : weekShortNames[6]) : "";
          console.log("repeats: " + value.repeats);
          value.detailDate = monthNames[todayDate.getMonth()] + " " + todayDate.getDate() + ", " + todayDate.getFullYear();
          switch (weekDay) {
            case value.MON_DAY:
              value.today = true;
              break;
            case value.TUE_DAY:
              value.today = true;
              break;
            case value.WED_DAY:
              value.today = true;
              break;
            case value.THU_DAY:
              value.today = true;
              break;
            case value.FRI_DAY:
              value.today = true;
              break;
            case value.SAT_DAY:
              value.today = true;
              break;
            case value.SUN_DAY:
              value.today = true;
              break;
            default:
              value.today = false;
          }
          if (value.today) $scope.todayCount++;
          console.log("today active: " + value.today);
          // check for tomorrow
          weekDay = $scope.weekdays[dayIndex];
          console.log("tomorrow: " + weekDay);
          switch (weekDay) {
            case value.MON_DAY:
              value.tomorrow = true;
              break;
            case value.TUE_DAY:
              value.tomorrow = true;
              break;
            case value.WED_DAY:
              value.tomorrow = true;
              break;
            case value.THU_DAY:
              value.tomorrow = true;
              break;
            case value.FRI_DAY:
              value.tomorrow = true;
              break;
            case value.SAT_DAY:
              value.tomorrow = true;
              break;
            case value.SUN_DAY:
              value.tomorrow = true;
              break;
            default:
              value.tomorrow = false;
          }
          if (value.tomorrow) $scope.tomorrowCount++;
          console.log("tomorrow active: " + value.tomorrow);
          if (value.CLASS_TIME) {
            var fromAndTo = value.CLASS_TIME.split("-");
            value.from = (fromAndTo.length > 0) ? fromAndTo[0].trim() : "";
            value.to = (fromAndTo.length > 1) ? fromAndTo[1].trim() : "";
            value.fromTime = new Date(todayDate.getFullYear() + "-" + (todayDate.getMonth() + 1) + "-" + todayDate.getDate() + " " + value.from);
            $scope.studentschedule.push(value);
          }
        });


        console.log("student schedule:");
        console.log($scope.studentschedule);
      });

