    $scope.dropClass = function(crn) {
      console.log("you clicked drop crn : " + crn);
      if ($scope.studentschedule.length == 0) {
        showAlert("No class registrations found !");
        return;
      }
      var course;
      angular.forEach($scope.studentschedule, function(value, key) {
        if (value.CRN == crn) {
          course = value;
          return;
        }
      });
      if (!course || !course.dropAllowed) {
        return;
      }
      var postdata = "";
      var crns = "";
      var regCodes = "";
      var action = "D";
      var startDates = "";
      var endDates = "";
      var firstTime = true;
      $scope.regResults = "";
      crns = course.CRN;
      regCodes = dropCode;
      startDates = "";
      endDates = "";
      postdata = "termCode=" + $scope.selectedtermCode + "&crns=" + crns + "&regCodes=" + regCodes + "&action=" + action + "&startDates=" + startDates + "&endDates=" + endDates;
      /*
      var data = {
        "p_out": [{
          "summary": {
            "error": "You cannot drop this class"
          }
        }]
      }
      */
      console.log(postdata)
      $rootScope.postAPI(config, bannerDropEndpoint, postdata, function(data) {
        //console.log(data);
        if (typeof data.p_out[0].summary == "string") {
          var dres = JSON.parse(data.p_out[0].summary);
          data.p_out[0].summary = dres;
        }
        $scope.regResults = data.p_out[0];
        if ($scope.regResults && $scope.regResults.summary && $scope.regResults.summary.error) {
          showAlert($scope.regResults.summary.error);
        } else if ($scope.regResults && $scope.regResults.summary && $scope.regResults.summary.results && $scope.regResults.summary.results.length > 0) {
          if ($scope.regResults.summary.results[0].status == 'S') {
            course.dropAllowed = false;
            showAlert($scope.regResults.summary.results[0].comments);
          } else {
            showAlert("Error: " + $scope.regResults.summary.results[0].comments);
          }
        }
      });
    }

