        datePicker.show(options, $scope.onSuccess, $scope.onError);
      } else {
        console.log("opening date");
        $('#searchbydate').datetimepicker({
          format: "MMM D, YYYY",
          icons: {
            time: "fa fa-clock-o",
            date: "fa fa-calendar",
            up: "fa fa-arrow-up",
            down: "fa fa-arrow-down",
            previous: 'fa fa-chevron-left',
            next: 'fa fa-chevron-right'
          }
        });
