      $scope.openDate();

      var value = "course";
      if (value == 'course') {
        $('#searchcourse').focus();
        /*for getting unique list of subjects*/
        $scope.coursenames = [];
        // get unique course names
        angular.forEach($scope.totalData, function(value, key) {
          var course = value.TITLE;


          var found = false;
          angular.forEach($scope.coursenames, function(value1, key1) {
            if (value1 === course) {
              found = true;
            }
          });
          if (!found) {
            $scope.coursenames.push(course);
          }
        });

        $scope.$apply();


      } else {
        $scope.openDate();
      }

    };


