      $scope.add = function() {
        //if (!$scope.isValid) return;
        if (!$scope.email) return;
        var totalCategoryAdmin = $scope.category.whatsupAdmins.map(function(value) {
          return value.toLowerCase();
        })
        if (totalCategoryAdmin.indexOf($scope.email.toLowerCase()) !== -1) {
          if (window.device) {
            navigator.notification.alert(
              'Email/Group already exists.', // message
              null, // callback
              '', // title
              'OK' // buttonName
            );
          } else {
            alert('Email/Group already exists.');
          }

          return;

        }
        if (!$scope.isValid) {
          // Validate the group name
          var groupUrl = "/unifydplatform/group/search/findByGroupEqualsIgnoreCase?group=" + $scope.email;
          $scope.callAPI(groupUrl, 'GET', '', function(res) {
            if (res && res.data && res.data._embedded && res.data._embedded.group && res.data._embedded.group.length > 0) {
              $scope.email = res.data._embedded.group[0].group;
              $scope.category.whatsupAdmins.push($scope.email);
              $scope.update(function(res) {
                $scope.email = '';
